// Set all basic structure pointers to 'safe' null values
void TransmissionAndSpeed_Init ( TransmissionAndSpeed_t* theObj)
{
	if (theObj != NULL)
	{
		// Allocate memory needed on heap
		int bufSize = TransmissionAndSpeed_BuffSize;  
		theObj->buf = (uint8_t *) malloc(bufSize);
		assert(theObj->buf);
		theObj->size = bufSize;
		// From the J2735 std, clause 6.64 Data Frame: DF_TransmissionAndSpeed
		//TransmissionAndSpeed ::= OCTET STRING (SIZE(2)) 
		//	-- Bits 14~16 to be made up of the data element
		//	-- DE_TransmissionState 
		//	-- Bits 1~13 to be made up of the data element
		//	-- DE_Speed
		// therefore we stuff 8191 for speed portion (Speed_Unavailable value)
		// and a transmission state of 'unavailable'(07) into the odd 2 bytes size
		// this results in a load of b"111" and 0x1FFF, or all ones
		theObj->buf[0] = 0xFF;
		theObj->buf[1] = 0xFF;
		theObj->_asn_ctx.ptr = NULL; // not used
	}
}

